export default {
    number: /^(-?)(\d+)(\.?)(\d*)$/,
    integer: /^(-?)(\d+)(\.?)$/,
    float: /^(-?)(\d+)(\.)(\d+)$/,
    positive: /^(\d+)(\.?)(\d*)$/,
    negative: /^(-)(\d+)(\.?)(\d*)$/,
    posInteger: /^(\d+)(\.?)$/,
    negInteger: /^(-)(\d+)(\.?)$/,
    posFloat: /^(\d+)(\.)(\d+)$/,
    negFloat: /^(-)(\d+)(\.)(\d+)$/
};
